changeSet.sql:
create table "user" ( id BIGINT not null, name VARCHAR(256) not null, last_name VARCHAR(256) not null, phone_number VARCHAR(12) not null, email VARCHAR(256) not null, password_hash VARCHAR(1256) not null, registration_date TIMESTAMP not null, nick_name VARCHAR(256) null );
alter table "user" add constraint PK_USER primary key (id);
create unique index user_PK on "user" (id);
