changeSet.sql:
create table lot_photo
(
	id             BIGINT        not null,
	lot_id         BIGINT        not null,
	reference      VARCHAR(2256) not null,
	reference_type VARCHAR(256)  not null
);

alter table lot_photo
	add constraint PK_LOT_PHOTO primary key (id);

create unique index photo_PK on lot_photo (id);

create index lot_to_photo_FK on lot_photo (lot_id);


alter table lot_photo
	add constraint FK_LOT_PHOT_TO_LOT foreign key (lot_id)
		references lot (id)
		on delete restrict on update restrict;
